import React, { useState } from "react";
import "./login.css";
import axios from "axios";

const Login = () => {
  const [user, setUser] = useState({
    email: "",
    password: "",
    isAccepted: false,
  });

  const login = () => {
    const { email, password, isAccepted } = user;
    if (email && password && isAccepted) {
      axios.post("http://192.168.52.1:9002/login", user)
        .then((res) => console.log(res))
        .catch((error) => console.log(error));
    } else {
      alert("Invalid data or terms not accepted");
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setUser((user) => ({
      ...u,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  return (
    <div className="login">
      <h1>Login</h1>
      <input
        type="text"
        name="email"
        value={user.email}
        placeholder="Enter your email"
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        value={user.password}
        placeholder="Enter your password"
        onChange={handleChange}
      />
      <div className="checkbox-wrapper">
        <input
          type="checkbox"
          id="checkbox"
          checked={user.isAccepted}
          onChange={handleChange}
        />
        <label htmlFor="checkbox">
          I agree to the <a href="https://www.w3schools.com">terms</a> and{" "}
          <a href="2www.google.com">conditions</a>
        </label>
      </div>
      <div className="button" onClick={login}>
        Login
      </div>
      <div>or</div>
      <div className="button">Register</div>
    </div>
  );
};

export default Login;
